function Fragment() {
    if (process.env.REACT_APP_IS_SERVER === '1') {
        return <Header />;
    } else {
        return (
            <FragmentClientContainer name="fragmentHeader">
                <Header />
            </FragmentClientContainer>
        );
    }
}

type Database interface {
    Connect() error
}

type Factory struct{}

func (f *Factory) CreateDatabase(dbType string) Database {
    switch dbType {
    case "MySQL":
        return &MySQLDatabase{}
    case "PostgreSQL":
        return &PostgreSQLDatabase{}
    default:
        return nil
    }
}

type Logger interface {
    Log(message string)
}

type LoggerFactory struct{}

func (lf *LoggerFactory) CreateLogger(loggerType string) Logger {
    switch loggerType {
    case "Console":
        return &ConsoleLogger{}
    case "File":
        return &FileLogger{}
    default:
        return nil
    }
}

type PaymentMethod interface {
    Pay(amount float32) string
}

type PaymentMethodFactory struct{}

func (pmf *PaymentMethodFactory) GetPaymentMethod(method string) (PaymentMethod, error) {
    switch method {
    case "CreditCard":
        return new(CreditCard), nil
    case "PayPal":
        return new(PayPal), nil
    default:
        return nil, errors.New("Invalid payment method")
    }
}

